  1: StepPhoneEmail,
  2: StepOtp,
};

const Login = () => {
  const [step, setStep] = useState(1); //default state
  const Step = steps[step];

  const onNext = () => {
    setStep(step + 1);
  };

  return <div>Login</div>;
};

export default Login;
